CREATE TABLE AUDIT (ID INTEGER NOT NULL, ENT_SEQ BIGINT NOT NULL, UPD_DATE DATE, UPD_DETL VARCHAR(255), ENTRY_ENT_SEQ BIGINT, PRIMARY KEY (ID, ENT_SEQ))
CREATE TABLE Characters (UNICODE VARCHAR(255) NOT NULL, CP_TYPE_JIS208 VARCHAR(255), CP_TYPE_UCS VARCHAR(255), FREQ INTEGER, GRADE INTEGER, JA_KUN VARCHAR(255), JA_ON VARCHAR(255), JLPT INTEGER, LITERAL VARCHAR(255), MEANING VARCHAR(255), NANORI VARCHAR(255), QUERY_CODE_DEROO VARCHAR(255), QUERY_CODE_FOUR_CORNER VARCHAR(255), QUERY_CODE_SH_DESC VARCHAR(255), QUERY_CODE_SKIP VARCHAR(255), RADICAL_CLASSICAL INTEGER, RADICAL_NELSON INTEGER, STROKE_COUNT INTEGER, PRIMARY KEY (UNICODE))
CREATE TABLE SENSE (ENT_SEQ BIGINT NOT NULL, ID INTEGER NOT NULL, ANT VARCHAR(255), DIAL VARCHAR(255), EXAMPLE VARCHAR(255), FIELD VARCHAR(255), GLOSS VARCHAR(255), MISC VARCHAR(255), POS VARCHAR(255), S_INF VARCHAR(255), STAGK VARCHAR(255), STAGR VARCHAR(255), XREF VARCHAR(255), ENTRY_ENT_SEQ BIGINT, PRIMARY KEY (ENT_SEQ, ID))
CREATE TABLE UNICODE (LITERAL VARCHAR(255), UNICODE VARCHAR(255) NOT NULL, ENT_SEQ VARCHAR(255) NOT NULL, ENTRY_ENT_SEQ BIGINT, PRIMARY KEY (UNICODE, ENT_SEQ))
CREATE TABLE ENTRY (ENT_SEQ BIGINT NOT NULL, KE_INF VARCHAR(255), KE_PRI VARCHAR(255), KEB VARCHAR(255), RE_INF VARCHAR(255), RE_NOKANJI VARCHAR(255), RE_PRI VARCHAR(255), RE_RESTR VARCHAR(255), REB VARCHAR(255), PRIMARY KEY (ENT_SEQ))
CREATE TABLE LSOURCE (ID BIGINT NOT NULL, LANG VARCHAR(255), LS_TYPE VARCHAR(255), LS_WASEI VARCHAR(255), VALUE VARCHAR(255), SENSE_ID INTEGER, SENSE_ENT_SEQ BIGINT, PRIMARY KEY (ID))
CREATE TABLE Character_DICNUMBERS (DR_TYPE VARCHAR(30), M_PAGE VARCHAR(6), M_VOL VARCHAR(6), unicode VARCHAR(5), VALUE VARCHAR(20), Character_UNICODE VARCHAR(255))
ALTER TABLE AUDIT ADD CONSTRAINT FK_AUDIT_ENTRY_ENT_SEQ FOREIGN KEY (ENTRY_ENT_SEQ) REFERENCES ENTRY (ENT_SEQ)
ALTER TABLE SENSE ADD CONSTRAINT FK_SENSE_ENTRY_ENT_SEQ FOREIGN KEY (ENTRY_ENT_SEQ) REFERENCES ENTRY (ENT_SEQ)
ALTER TABLE UNICODE ADD CONSTRAINT FK_UNICODE_ENTRY_ENT_SEQ FOREIGN KEY (ENTRY_ENT_SEQ) REFERENCES ENTRY (ENT_SEQ)
ALTER TABLE LSOURCE ADD CONSTRAINT FK_LSOURCE_SENSE_ENT_SEQ FOREIGN KEY (SENSE_ENT_SEQ, SENSE_ID) REFERENCES SENSE (ENT_SEQ, ID)
ALTER TABLE Character_DICNUMBERS ADD CONSTRAINT FK_Character_DICNUMBERS_Character_UNICODE FOREIGN KEY (Character_UNICODE) REFERENCES Characters (UNICODE)
